import React, {useRef} from 'react';
import { Modal, Form, Button } from 'react-bootstrap';

export default function ConversationModal({closeShow}) {
  const idRef = useRef();
  const nameRef = useRef();
  const createNewConversation = (e) => {
    e.preventDefault();
    closeShow();
  }
  return (
    <>
    <Modal.Header closeButton={true}>Create new Conversation</Modal.Header>
    <Modal.Body>
      <Form onSubmit={createNewConversation}>
        <Form.Group>
            <Form.Label>
                Id
            </Form.Label>
            <Form.Control type="text" placeholder="Enter ID" ref = {idRef} required/>
        </Form.Group>
        <Form.Group>
            <Form.Label>
                Name
            </Form.Label>
            <Form.Control type="text" placeholder="Enter ID" ref = {nameRef} required/>
        </Form.Group>
        <Button onClick={createNewConversation} className="mt-3">
            Create
        </Button>
    </Form>
    </Modal.Body>
    </>
  )
}
